import initSqlJs from ''sql.js'';
import fs from ''fs'';

const SQL = await initSqlJs\(\);
const buffer = fs.readFileSync\(''./data/metrics.db''\);
const db = new SQL.Database\(buffer\);

// Check if table exists and has data
console.log\(''=== Table Info ===''\);
const tables = db.exec\(""SELECT name FROM sqlite_master WHERE type=''table''""\);
console.log\(''Tables:'', JSON.stringify\(tables\)\);

console.log\(''\\n=== Row Count ===''\);
const count = db.exec\(''SELECT COUNT\(*\) as count FROM request_metrics''\);
console.log\(''Count:'', JSON.stringify\(count\)\);

console.log\(''\\n=== Recent Rows ===''\);
const rows = db.exec\(''SELECT id, timestamp, user_email, conversational_model, input_tokens, output_tokens FROM request_metrics ORDER BY id DESC LIMIT 5''\);
console.log\(''Rows:'', JSON.stringify\(rows, null, 2\)\);

console.log\(''\\n=== Current SQLite Time ===''\);
const time = db.exec\(""SELECT datetime\(''now''\) as utc, datetime\(''now'', ''localtime''\) as local""\);
console.log\(''Time:'', JSON.stringify\(time\)\);

db.close\(\);
")

